    return (
        <main className="home" >
            <Titulos text="Selecione o filme" />
            <ul className="cartaz-box" >
                {movies.map((movie) => (
                <Poster
                    src={movie.posterURL}
                    alt={`Poster de ${movie.title}`}
                />))}
            </ul>
        </main>
    )
}
