import "./styles.css"
import Titulos from "../infos/Titulos/Titulos.js";
import axios from "axios";
import { useState, useEffect } from "react";
import Dia from "./Dia/Dia";
import { useParams } from "react-router-dom";
import Loading from "../infos/Loading/Loading";

export default function Sessao() {
    const { filmId } = useParams();
    const [sessoes, setSessoes] = useState(null);
    useEffect(() => {
        axios.get(`https://mock-api.driven.com.br/api/v4/cineflex/movies/${filmId}/showtimes`)
        .then((response) => setSessoes(response.data))
        .catch((error) => alert(error))
    }, [])

    if (sessoes === null) {
        return (
            <Loading />
        )
    }

    return(
        <article className="Sessoes">
            <Titulos text="Selecione o horÃ¡rio"/>
            <section className="dia-box" >
                {sessoes.days.map((dia) => (
                    <Dia
                        weekday={dia.weekday}
                        date={dia.date}
                        showtimes={dia.showtimes}
                        key={dia.id}
                    />
                ))}
            </section>
        </article>
    )
}
